mapper.select:
SELECT COUNT(*)
FROM board_join_me
WHERE region = #{keyword}

SELECT COUNT(*)
FROM board_join_me
WHERE user_id = #{userId}

SELECT *
FROM board_join_me AS b
         JOIN user AS u ON b.user_id = u.user_id
WHERE region = #{word}
ORDER BY join_me_id DESC LIMIT #{startNum}, #{pageSize}

SELECT *
FROM board_join_me AS b
         JOIN user AS u ON b.user_id = u.user_id
WHERE b.user_id = #{userId}
ORDER BY join_me_id DESC LIMIT #{startNum}, #{pageSize}

SELECT *
FROM board_join_me AS b
         JOIN join_me_apply AS j ON b.join_me_id = j.join_me_id
WHERE j.user_id = #{userId}
ORDER BY b.join_me_id DESC LIMIT #{startNum}, #{pageSize}

mapper.update:
UPDATE board_join_me
SET title=#{title},
    content=#{content},
    start_date=#{startDate},
    end_date=#{endDate},
    region=#{region},
    capacity=#{capacity}
WHERE join_me_id = #{joinMeId}
  AND user_id = #{userId}

UPDATE board_join_me
SET view= view +1
WHERE join_me_id=#{joinMeId}

UPDATE board_join_me
SET closed=#{closed}
WHERE join_me_id = #{joinMeId}

UPDATE board_join_me
SET current_member_count = #{currentMemberCount}
WHERE join_me_id = #{joinMeId}
  AND user_id = #{userId}

mapper.delete:
DELETE
FROM board_join_me
WHERE join_me_id = #{joinMeId}
  AND user_id = #{userId}
